mapper.insert:
insert
	into calendar
	(
		  cal_id
		, cal_title
		, cal_start
		, cal_end
		, cal_description
		, cal_type
		, username
		, backgroundcolor
		, textcolor
		, allday
	)
	values
	(
		  #{_id}
		, #{title}
		, #{start}
		, #{end}
		, #{description}
		, #{type}
		, #{username}
		, #{backgroundColor}
		, #{textColor}
		, #{allDay}
    )
