mapper.insert:
insert
	into task
			(
			  task_no
  	        , subject_no
			, id
			, task_title
			, task_content
			, start_date
			, end_date
			)
			values
			(
			    seq_taskno.nextval
			  , #{subjectNo}
			  , #{instructorId}
			  , #{taskTitle}
			  , #{taskContent}
			  , #{startDate}
			  , #{endDate}
			)

mapper.update:
update task
	set task_title = #{taskTitle}
	  , task_content = #{taskContent}
	  , start_date = to_date(#{startDate}, 'RR-MM-DD')
	  , end_date = to_date(#{endDate}, 'RR-MM-DD')
where task_no = #{taskNo}

update task_file
set origin_name = #{originName}
  , change_name = #{changeName}
where task_no = #{taskNo}

update task
	set status = 'Y'
where task_no = #{taskNo}

update task_file
	set status = 'Y'
where task_no = #{taskNo}

update task_submit
	set status = 'Y'
where task_no = #{taskNo}

update task_submit
	set status = 'Y'
where task_no = #{taskNo} and student_id = #{studentId}

update exam
	set exam_time = #{examTime}
where test_no = #{testNo}

update exam_answer
	set score = #{score}
	  , status = 'Y'
where test_no = #{testNo} and student_id = #{studentId}
