mapper.insert:
insert
    into task
        (
           task_no
         , subject_no
         , id
         , task_title
         , task_content
         , start_date
         , end_date
        )
        values
        (
            seq_task_no.nextval
          , #{subjectNo}
          , #{instructorId}
          , #{taskTitle}
          , #{taskContent}
          , #{startDate}
          , #{endDate}
        )
