mapper.update:
	update
		member
	set
		name=#{name}
	  , phone=#{phone}
	  , email=#{email}
	  ,	address=#{address}
  	where
  		id=#{id}

mapper.select:
select distinct m.id , a.status "status" , attendance_date from attendance a join member m on(a.id = m.id) where m.id = #{id}
and a.status = 'Y'
and a.status = 'N'
