mapper.insert:
insert
into alarm
values
(
seq_alarm_no.nextval
, #{id}
, #{alarmContent}
, #{alarmType}
, #{status}
, sysdate
)

mapper.update:
update
member
set
name=#{name}
,birth=#{birth}
,email=#{email}
,phone=#{phone}
, post = #{post}
, address = #{address}
, detail_Address = #{detailAddress}
where id = #{id} and status= 'Y'

update
member
set subject = #{subject}
where
id = #{id}
and sort = 2
