mapper.insert:
insert
  into exam_answer
     (
       test_no
     , student_id
     , a1
     , a2
     , a3
     , a4
     , a5
     , status
     )
values
     (
       #{ testNo }
     , #{ studentId }
     , #{ a1 }
     , #{ a2 }
     , #{ a3 }
     , #{ a4 }
     , #{ a5 }
     , 'Y'
     )

mapper.update:
update task_submit
	set submit_content = #{submitContent}
where task_no = #{taskNo} and student_id = #{studentId}

update alarm
	set status = 'Y'
where alarm_no = #{alarmNo}
